NODE.context:
[% 
 if(defined $$VARS{themecontainer})
 {
  $$CURRENTNODE{parent_container} = $$VARS{themecontainer};
 } else {
  my $SETTINGS_NODE = $DB->getNode('system settings', 'setting');
  my $SYSSETTINGS = getVars $SETTINGS_NODE;
  if (my $parent = $$SYSSETTINGS{generalParent_container}) {
    $$CURRENTNODE{parent_container} = $parent;
  }
 }
"";
%]
