NODE.field:
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;
my $query = $this->get_query;
my $THEME = $this->get_theme;
my %HTMLVARS = %{ $this->get_htmlvars };
my $VARS = $this->get_vars;
my $DB = $this->get_nodebase;
my %GLOBAL;


	if (defined($$THEME{nodeletParent_container})) {
		$$CURRENTNODE{parent_container} = $$THEME{nodeletParent_container};
	} else {
		my $parent;
		if (defined($parent = $HTMLVARS{generalNodelet_container}) &&
		  $parent != $$CURRENTNODE{parent_container}) {
			$$CURRENTNODE{parent_container} = $parent;
		}
	}
"";

%]CONTAINED_STUFF
