NODE.code:
return if $$USER{title} eq "Guest User";

my $disp = $query->param("displaytype");
my @nodes;
my $str;

$disp ||= "display";

# This will recurse up the nodetype inheritance hierarchy
# and find the appropriate display and edit pages for this
# node.
push @nodes, getPages($NODE);

foreach my $N (@nodes)
{
	if ($disp eq $$N{displaytype}){
		$str .= $$N{displaytype} ." ";
	} else {
		next if ($$N{displaytype} eq 'edit' and not canUpdateNode($USER, $NODE));
		$str .= linkNode ($NODE, $$N{displaytype}, {displaytype => $$N{displaytype}}) . " ";
	}
}


# If a group editor is open, add a link to add this node to
# the group.  We will need to check if the nodegroup has
# a restriction on what nodetypes can be in the group.
if ($$VARS{group}) {
	my $GR = $DB->getNodeById($$VARS{group}, 'light');

	if(canUpdateNode($USER, $GR))
	{
		$str .=
"<SCRIPT language=\"javascript\">
	function updateGroup() {
	window.open('" .
		urlGen({node_id => $$VARS{group},
			displaytype => 'editor',
			add => getId ($NODE)}, "noquotes") .
		"','". $$VARS{group} ."',
		'');	
	}
</SCRIPT>
<A href=\"javascript:updateGroup()\">add to &quot;$$GR{title}&quot;</a>";
	}
}




$str.="\n<SCRIPT language=\"javascript\">
       function confirmDelete(){
          if(confirm(\"Delete '$$NODE{title}'?\")){
		parent.location=" .
		urlGen({node_id=>getId($NODE),op=>"nuke"}) .
		"; }
        }
       </SCRIPT>\n
	<br><font size=1>
	<a href='javascript: confirmDelete();'>delete</a>
	</font>" 
			if(canDeleteNode($USER, $NODE));                                                                                          

$str;
