NODE.field:
# This provides links to display, edit, clone, or delete 
# the current node if the user has the authority to do so.  

my $disp = $query->param("displaytype");
my $str;
my @links;

my $CLONER = getNode("node cloner", "restricted_superdoc");
my $PARAMS = { "srcnode_id" => $$NODE{node_id}, 
  "node_id" => $$CLONER{node_id},
  "newname" => "NEWNAME" };

my $url = urlGen($PARAMS);
my $script = "
<script language=\"javascript\">

function cloneNode()
{
  var newname = prompt(\"Cloning node \\\"$$NODE{title}\\\"\\nEnter a new name:\", \"\");

  if(newname)
  {
    var url = $url;

    url = url.replace(/NEWNAME/, newname);
    url = url.replace(/ /g, \"%20\");
    parent.location = url;
  }
}

</script>";

$str = $script;

$disp ||= "display";

# If you want to have other default display links in the
# adminbar, add them to this array.
my @displaytypes = ("display", "edit", "permissions" );

foreach my $type (@displaytypes)
{
  if ($disp eq $type)
  {
    push @links, "<img src=images/icon_".$type."_current.gif alt=".$type." border=0>";
  } 
  else
  {
    push @links, linkNode ($NODE, "<img src=images/icon_".$type.".gif  alt=".$type." border=0>", 
      {displaytype => $type}) . " ";
  }
}

$str.="<table width=100% border=0 cellpadding=3 bgcolor=#FFFFFF cellspacing=1>
         <tr bgcolor=#ffffff> 
           <td bgcolor='#FFCCCC' align='center'>".$links[0].
          "</td><td bgcolor='#FF9999' align='center'>".$links[1].
          "</td><td bgcolor='#FFCCCC' align='center'>".$links[2].
          "</td></tr>";


$str .= "<tr><td bgcolor='#FF9999' align='center'><a href='javascript: cloneNode();'><img src=images/icon_clone.gif alt=clone border=0></a></td>
<td bgcolor='#FFCCCC' align=center>";
$str.= htmlcode('clonetotype')."</td>";
# if a user can delete the node, this places a delete icon in the box.
if($NODE->hasAccess($USER, "d"))
{
$str.="
<SCRIPT language=\"javascript\">
  function confirmDelete(){
    if(confirm(\"Delete '$$NODE{title}'?\")){
      parent.location=" .
        urlGen({node_id=>getId($NODE),op=>"nuke"}) .
		"; }
        }
</SCRIPT>
<td bgcolor='#FF9999' align=center><a href='javascript: confirmDelete();'><img src='images/icon_delete.gif' alt='delete' border=0></a></td></tr>";
}
else
 {
  $str.="<td bgcolor='#FF9999' align=center>&nbsp;</td></tr>";
 }

# If a group editor is open, add a link to add this node to
# the group.  We will need to check if the nodegroup has
# a restriction on what nodetypes can be in the group.
my $GROUP;
if ($$VARS{group} &&
  ($GROUP = getNode($$VARS{group}, 'light')) &&
  $GROUP->hasAccess($USER, "w"))
{
  $str .=
"<SCRIPT language=\"javascript\">
  function updateGroup() {
  window.open('" . urlGen({node_id => $$VARS{group},
			displaytype => 'editor',
			add => getId($NODE)}, "noquotes") .
		"','". $$VARS{group} ."',
		'');	
  }
</SCRIPT>
<tr><td bgcolor='#FFCCCC' align=center colspan=3><font size=2><A href=\"javascript:updateGroup()\">add to &quot;$$GROUP{title}&quot;</a></font></td></tr>";
}

$str.="</table>";

$str;
