NODE.code:
# This diplays inherited values for a nodetype.  This
# checks to see if the given field has any inherited
# values.
my ($field) = @_;
my $str = "";

return "" unless ((isNodetype($NODE)) && (defined $field));

if($field eq "sqltable")
{
  $str .= "$NODETYPES{$$NODE{extends_nodetype}}{sqltablelist}"
  if($NODETYPES{$$NODE{extends_nodetype}});
}
elsif(($field eq "grouptable") && ($$NODE{$field} eq ""))
{
  my $gt = "$NODETYPES{$$NODE{node_id}}{$field}";
  $str .= $gt if ($gt ne "");
}
elsif($$NODE{$field} eq "-1")
{
  my $node = getNodeById($NODETYPES{$$NODE{node_id}}{$field});
  my $title = $$node{title} if (defined $node);
  $title ||= "none";
  $str .= $title;
}

$str = " ( Inherited value: $str )" if ($str ne "");
