NODE.field:
my ($class, $forType) = @_;
my $perms;
my $parent;

if($forType)
{
  if($$NODE{extends_nodetype} > 0)
  {
    return "Error. Not a nodetype.\n"
      unless($NODE->isNodetype());
    $parent = getType($$NODE{extends_nodetype});
  }
}
else
{
  $parent = getType($$NODE{type_nodetype});
}

return "No parent" unless($parent);

$perms = $parent->getDefaultTypePermissions($class);
return $perms . "&nbsp;";
