NODE.code:
my ($class, $forType) = @_;
my $perms;
my $parent;

if($forType)
{
  if($$NODE{extends_nodetype} > 0)
  {
    return "Error. Not a nodetype.\n" unless(isNodetype($NODE));
    $parent = getType($$NODE{extends_nodetype});
  }
}
else
{
  $parent = getType($$NODE{type_nodetype});
}

$perms = $DB->getDefaultTypePermissions($parent, $class);
$perms ||= "No parent";

return $perms;
