NODE.field:
# This generates an HTML table that contains the fields
# of a database table.  The output is similar to what
# you get when 'show columns from $table' is executed.
my ($table, $edit) = @_;
my $query = $this->get_query;
my $DB = $this->get_nodebase;
my @fields = $DB->getFieldsHash($table);
my $field;
my $fieldname;
my $value;
my $str;

use Template;
my $t = Template->new  || die $Template::Error;

my $template=q{<table class="displayTable" width="400">
<tr><td style="background-color: #cccccc">Field name</td>
[% IF edit %]
<td  style="background-color: #cccccc">Remove Field?</td>
[% END %]
</tr>
[% FOREACH field IN fields %]
<tr><td>[% field %]</td>
[% IF edit %]
<td><input type="checkbox" name="[% field %]REMOVE" value="REMOVE"
	   /></td>
[% END %]
</tr>
[% END %]
</table>

[% IF edit %]
<p>Add new field:</p>
<fieldset class="addNewField">
<label for="fieldname_new">Field Name: </label>
<input type="text" name="fieldname_new" value="" size="30"
       maxlength="50" id="fieldname_new" />

<label for="fieldtype_new">Field type: </label>
<input type="text" name="fieldtype_new" value="" size="10"
       maxlength="10" id="fieldtype_new" /> (i.e. int(11), char(32), text, etc.)
<label for="fielddefault_new"> Default value: </label>
<input type="text" name="fielddefault_new" value="" size="50"
       maxlength="50" id="fielddefault_new" />

<label><input type="checkbox" name="fieldprimary_new" value="primary"
/>Primary Key?</label>
</fieldset>
[% END %]
};

my @entries =  map { $$_{Field} } @fields;

my %data = ( edit => $edit, fields => \@entries );

$t->process( \$template, \%data, \$str) || die $t->error;

$str;
