NODE.code:
my $SETTINGS = getVars $NODE;
my $str;
my ($keyclr, $valclr) = ("#CCCCFF", "#DEDEFF");


my @skeys = keys %$SETTINGS;
if (not @skeys) { 
  return "<i>the node's settings are empty</i><br>\n";
}

@skeys = sort {$a cmp $b} @skeys;

$str.="<TABLE width=100% cellpadding=1 cellspacing=0>\n";
$str.="<TR><TH>Setting</TH><TH>Value</TH></TR>\n";
foreach (@skeys) {
  $str.= "<TR><TD bgcolor=$keyclr>$_</TD><TD bgcolor=$valclr>$$SETTINGS{$_}</TD></TR>\n";  
}
$str .="</TABLE>\n";
