NODE.field:
# This generates the html to display the vars hash of a node.
#
# Parameters
#   $varfield - (optional) The field of the node that contains the vars
#       hash this is to edit.  Defaults to 'vars'.
#   $N - (optional) the node object that we are editing.  Defaults to $NODE
#

my ($varsfield, $N) = @_;
$varsfield ||= 'vars';
$N ||= $NODE;
my $html = "<table border=0 width='80%'>\n";
my $vars = $N->getHash($varsfield);

return "<i>No settings</i>\n" unless($vars);

my @keys;
@keys = sort keys %$vars;

my $keyColor = $$THEME{keyColor};
my $valueColor = $$THEME{valueColor};

$keyColor ||= "#cc99cc";
$valueColor ||= "#cc99ff";

$html .= "<tr><td bgcolor='$keyColor'><b>Keys</b></td>\n";
$html .= "<td bgcolor='$valueColor'><b>Values</b></td></tr>\n";

foreach my $key (@keys)
{
  $html .= "<tr><td colspan=3>New Key/Value:</td></tr>\n" if($key eq "NEWFIELD");
  $html .= "<tr><td bgcolor='$keyColor' nowrap>$key</td>\n";
  $html .= "<td bgcolor='$valueColor' nowrap>$$vars{$key}";
  my $link = linkVar($key, $$vars{$key});
  $link = "&nbsp;&nbsp;($link)" if($link && $link ne "");
  $html .= " $link </td></tr>\n";
}

$html .= "</table>";
return $html;
