NODE.field:
use Everything::FormMenu;
my ($who, $default) = @_;
my $menu;
my $str;
my @menus;
my @perms;
my $field = "";

$field = "default" if($default);
$field .= $who . "access";

@perms = split '', $$NODE{$field};

for(my $i = 0; $i < @perms; $i++)
{
  # We don't want to deal with the "create" case, if it
  # is not the nodetype defaults.
  next if($i == 4 && not $default);

  my $options = { 'i' => "inherit", '-' => "disable" };
  my $name = $who;

  $name = "default_" . $name if($default);

  $menu = new Everything::FormMenu();
  # The order is always "rwxdc"
  if($i == 0)
  {
    $name .= "_read";
    $$options{r} = "read";
  }
  elsif($i == 1)
  {
    $name .= "_write";
    $$options{w} = "write";
  }
  elsif($i == 2)
  {
    $name .= "_exec";
    $$options{x} = "execute";
  }
  elsif($i == 3)
  {
    $name .= "_delete";
    $$options{d} = "delete";
  }
  elsif($i == 4)
  {
    $name .= "_create";
    $$options{c} = "create";
  }

  $menu->addHash($options);
  push @menus, $menu->writePopupHTML($query, $name, $perms[$i]);
}

$str = join "</td>\n<td bgcolor=#999999>", @menus;

return $str;
