NODE.field:
# This generates the html to edit the vars hash of a node.
#
# Parameters
#   $varfield - (optional) The field of the node that contains the vars
#       hash this is to edit.  Defaults to 'vars'.
#   $N - (optional) the node object that we are editing.  Defaults to $NODE
#

my ($varsfield, $N) = @_;
my $THEME = $this->get_theme || {};
$varsfield ||= 'vars';
$N         ||= $this->get_node;

my $html = "<table border=0>\n";
my $vars = $N->getHash($varsfield);

my @keys;
@keys = sort keys %$vars if($vars);

my ($keyColor, $valueColor, $removeColor)
	= @{ $THEME }{qw( keyColor valueColor removeColor )};

$keyColor    ||= '#cc99cc';
$valueColor  ||= '#cc99ff';
$removeColor ||= '#999999';

$html .= "<tr><td bgcolor='$removeColor'>Remove</td>\n";
$html .= "<td bgcolor='$keyColor'><b>Keys</b></td>\n";
$html .= "<td bgcolor='$valueColor'><b>Values</b></td></tr>\n";

my $numkeys = @keys;
push @keys, 'NEWFIELD';

for my $i (0 .. $numkeys)
{
  my $key = $keys[$i];
  $html .= "<tr><td colspan=3>New Key/Value:</td></tr>\n"
    if $i == $numkeys;
  $html .= "<tr><td bgcolor='$removeColor'>";
  $html .= $this->bindFormObject('RemoveVarCheckbox', $N, $varsfield, $key)
    unless $i == $numkeys;
  $html .= "</td><td bgcolor='$keyColor' nowrap>";
  $html .= $this->bindFormObject('VarsTextField', $N, $varsfield, $key, 1);
  $html .= "</td><td bgcolor='$valueColor' nowrap>\n";
  $html .= $this->bindFormObject('VarsTextField', $N, $varsfield, $key, 0);
  $html .= " " . $this->linkVar($key, $$vars{$key});
  $html .= " </td></tr>\n";
}

$html .= "</table>";
$html;
