NODE.code:
return "<i>you can't update this node</i>" unless canUpdateNode($USER, $NODE);
my $SETTINGS = getVars($NODE);
my @params = $query->param;
my $str=""; 

foreach (@params) {
  if (/setsetting_(\w*)$/) {
    $$SETTINGS{$1}=$query->param("setsetting_$1");    
  }

}

foreach (@params) {
  if (/delsetting_(\w*)$/) {
    delete $$SETTINGS{$1};
  }
}


if ($query->param("newsetting") ne "" and $query->param("newval") ne ""){
  my $title = $query->param("newsetting");
  $title =~ s/\W//g;
  $$SETTINGS{$title} = $query->param("newval");
}

setVars ($NODE, $SETTINGS);
my @skeys = keys %$SETTINGS;
@skeys = sort {$a cmp $b} @skeys;

my ($keysize, $valsize) = (15, 30);
my ($keyclr, $valclr) = ("#CCCCFF", "#DEDEFF");


$str.="<TABLE width=100% cellpadding=1 cellspacing=0>\n";
$str.="<TR><TH>Remove</TH><TH>Setting</TH><TH>Value</TH></TR>\n";
foreach(@skeys) {
$str.="<TR><TD><input type=checkbox name=delsetting_$_></td>
   <TD bgcolor=$keyclr><b>$_</b></TD>
   <TD bgcolor=$valclr><INPUT TYPE=text NAME=setsetting_$_ VALUE='$$SETTINGS{$_}' size=$valsize></TD></TR>\n";
}
$str.="<TR><td></td>
  <TD bgcolor=$keyclr><INPUT TYPE=text NAME=newsetting size=$keysize></TD>
  <TD bgcolor=$valclr><INPUT TYPE=text NAME=newval size=$valsize></TD></TR>\n";
$str.="</TABLE>\n";


$str;
