NODE.code:
my $id = getId $NODE;

my $str = "

<SCRIPT LANGUAGE=\"JavaScript1.1\">

function saveForm()
{
    with (document.forms[$id].elements[0]) {
        for(i=0;i<length;i++) {
            document.forms[$id].elements[1+i].value=options[i].value;
        }
    }
    return true;
}

function swapUp()
{
    with (document.forms[$id].elements[0]) {
        x=selectedIndex;
        if(x == -1) { return; }
        if(options.length > 0) {
                tmp = new Option(options[x].text, options[x].value);
                options[x] = options[x-1];
                options[x-1] = tmp;
                options[x-1].selected = true;
        }
    }
}

function swapDn()
{
    with (document.forms[$id].elements[0]) {
        x=selectedIndex;
        if(x == -1) { return; }
        if(x+1 < options.length) {
                tmp = new Option(options[x].text, options[x].value);
                options[x] = options[x+1];
                options[x+1] = tmp;
                options[x+1].selected = true;
        }
    }
}

function deleteOp()
{
    with (document.forms[$id].elements[0]) {
        x=selectedIndex;
        if(x == -1) { return; }
        for(i=x;i<options.length;i++) {
            options[i]=options[i+1];
        }
        options[options.length] = null;
        if(x==0) { x=1; }
        options[x-1].selected = true;
    }
}
function zoomOp()
{
    with (document.forms[$id].elements[0]) {
        if(selectedIndex == -1) { return; }
        window.open('index.pl?node_id=' + options[selectedIndex].value,
                'hernandez','');
    }
}

</SCRIPT>";

$str .= "<FORM METHOD=\"GET\" NAME=\"$id\" onSubmit=\"saveForm()\">";

my $GROUP = $$NODE{group};

        $GROUP ||= [];

        #generate the select box
        $str .= " <SELECT NAME=\"group\" size=7>\n";
        foreach my $item (@$GROUP) {
                my $ITEM = $DB->getNodeById($item, 'light');

                $str .= " <OPTION VALUE=\"" . getId ($ITEM) . "\">$$ITEM{title}\n";
        }
        $str .= "</SELECT><BR>";

        #generate the hidden elements
        for (my $i = 0; $i < (5 + @$GROUP); $i++) {
                $str .= "<INPUT type=hidden name=$i value=\"\">\n";
        }

	$str .= $query->hidden('node_id', getId $NODE) .
		$query->hidden('displaytype');

$str .= '

<A HREF="javascript:deleteOp();">delete</A>
<A HREF="javascript:swapUp();">up</A>
<A HREF="javascript:swapDn();">down</A>
<A HREF="javascript:zoomOp();">view</A>

<INPUT TYPE=submit BORDER=0 
        VALUE="Save" onClick="javascript:saveForm()">';

#$str .= "</FORM>";

$str;
