NODE.field:
my $NODELET = shift @_;
my $type = shift @_;
my $USER = $this->get_user;
$type ||= 'nodelet';
$NODELET = $this->get_nodebase->getNode( $NODELET, $type );

# If the user can't "execute" this nodelet, we don't let them see it!
return undef
  unless ( defined $NODELET && $NODELET->hasAccess( $USER, "x" ) );

my $html;

my $block = $this->get_nodebase->getNode( 'nodelet_block', 'htmlsnippet');
# Make sure the nltext is up to date
$this->updateNodelet($NODELET);
return unless ( $$NODELET{nltext} =~ /\S/ );

return $block->run( { ehtml => $this, args => [ $NODELET->get_title, $NODELET->get_nltext ] } );
return;
