NODE.field:
my ($type) = @_;

return "" unless($type);
my $DB = $this->get_nodebase;
my $TYPE = $DB->getType($type);
return "" unless($TYPE);

my $tables = $DB->get_storage->retrieve_nodetype_tables($TYPE->get_node_id, 1);
my $str;
foreach my $table (@$tables)
{
  $str .="$table table fields:<ul>\n";
  $str .= "<li>\n";
  $str .= join "\n<li>", sort $DB->getFields($table);
  $str .= "\n</ul>";
}

$str;
