NODE.field:
my ($field) = @_;

my $G = $$NODE{$field};

if(($G eq "") || ($G == 0)) { return "none"; }
elsif($G == -1) { return "parent"; }

getRef $G;
return "none" unless ref $G;

my $str = linkNode($G) . "($$G{type}{title})";
return $str unless ($$G{group});

$str .= "\n<ol>\n";
my $groupref = $$G{group};
foreach my $item (@$groupref) {
	my $N = $DB->getNodeById($item, 'light');
	$str .= "<li>" . linkNode ($N) . "($$N{type}{title})\n";
}
$str .= "</ol>\n";

$str;
