NODE.field:
my ($field) = @_;
my $NODE = $this->get_node;
my $G = $$NODE{$field};
my $DB = $this->get_nodebase;

if(($G eq "") || ($G == 0)) { return "none"; }
elsif($G == -1) { return "parent"; }

$DB->getRef( $G );
return "none" unless ref $G;

my $str = $this->link_node($G) . "($$G{type}{title})";
return $str unless ($$G{group});

$str .= "\n<ol>\n";
my $groupref = $$G{group};
foreach my $item (@$groupref) {
	my $N = $DB->getNodeById($item, 'light');
	$str .= "<li>" . $this->link_node ($N) . "($$N{type}{title})\n";
}
$str .= "</ol>\n";

$str;
