NODE.field:
my ($mini, $align, $valign, $image) = @_;

return "" unless($mini);

my $str;
my $display = $query->param('displaytype');

$display ||= 'display';
$image ||= "images/minime.gif";

$str .= "<image src='$image'";
$str .= " valign='$valign'" if($valign);
$str .= " align='$align'" if($align);
$str .= " border=0>";

$mini = $DB->getNode($mini);

return "" unless($$mini{mini_nodelet});
return "" unless(getNodeById($$mini{mini_nodelet})->hasAccess($USER, "x"));

my $link = linkNode($NODE, $str, { 'op' => 'insertMini','nodelet' => $$mini{node_id}, 'displaytype' => $display } ); 

$link;
