NODE.field:
return "you have this off" 
if $$VARS{nodelets_off};
return if (($$USER{title} eq "Guest User") and not($HTMLVARS{GUshownodelets}) );
my $str;

my @nodelets = @{ $USER->getNodelets($HTMLVARS{default_nodeletgroup}) };

return "no nodelets!" unless @nodelets;

if ($$THEME{nodeletMCTableAttr}){
  $str .= "<TABLE ".$$THEME{nodeletMCTableAttr}.">\n";
} else {
  $str .= "<TABLE width=150 cellpadding=1 cellspacing=1>\n";
}

foreach (@nodelets)
{
  my $NODELET = $DB->getNodeById($_);
  my $nl = insertNodelet($NODELET);
  $str .= "<tr><td>" . $nl . "</td></tr>\n" if($nl);
}

$str .="</TABLE>";
$str;
