NODE.field:
return "you have this off" if $$VARS{nodelets_off};
return if (($$USER{title} eq "Guest User") and not($HTMLVARS{GUshownodelets}) );
my $str;
my $align = $$VARS{nodelet_align};

$align ||= "right";
my @nodelets;
@nodelets = split ",",$$VARS{nodelets} if exists ($$VARS{nodelets});

my $NODELETGROUP = getNode($$VARS{nodelet_group}) if exists $$VARS{nodelet_group};

if($NODELETGROUP and
  $$NODELETGROUP{type}{title} eq 'nodeletgroup')
{
  push @nodelets, @{ $$NODELETGROUP{group} } unless @nodelets;
}

if (not @nodelets)
{ 
  #push default nodelets on
  my $DEFAULT = $DB->getNode($HTMLVARS{default_nodeletgroup});
  @nodelets = @{ $$DEFAULT{group} }; 
}

return "no nodelets!" unless @nodelets;

if ($$THEME{nodeletMCTableAttr}){
  $str .= "<TABLE ".$$THEME{nodeletMCTableAttr}.">";
} else {
  $str .= "<TABLE width=150 cellpadding=1 cellspacing=1>";
}

foreach (@nodelets)
{
  my $NODELET = $DB->getNodeById($_);
  my $nl = insertNodelet($NODELET);
  $str .= "<tr><td>" . $nl . "</td></tr>" if($nl);
}

$str .="</TABLE>";
$str;
