NODE.code:
return "you have this off" if $$VARS{nodelets_off};

my $str;
my $align = $$VARS{nodelet_align};
my @nodelets = split ",",$$VARS{nodelets} if exists ($$VARS{nodelets});


$align ||= "right";
my ($NODELETGROUP) = $DB->getNodeById($$VARS{nodelet_group}) if exists $$VARS{nodelet_group};
if ($NODELETGROUP and $$NODELETGROUP{type}{title} eq 'nodeletgroup') { push @nodelets, @{ $$NODELETGROUP{group} };  }


if (not @nodelets) {
	#push default nodelets on

	my ($DEFAULT) = $DB->getNodeWhere({title => "default nodelets"},
	  $DB->getType('nodeletgroup'));		
	@nodelets = @{ $$DEFAULT{group} }; 
}
return "no nodelets!" unless @nodelets;

$str .= "<TABLE cellspacing=1 cellpadding=1>";

foreach (@nodelets) {
	my $NODELET = $DB->getNodeById($_);
	my $nl = insertNodelet($NODELET);
	#$nl = parseCode($nl);
	$str .= "<tr><td>" . $nl . "</td></tr>";
}


$str .="</TABLE>";
$str;
