NODE.code:
my ($field) = @_;

#like all good password fields, we should have a confirm
my $name = "$$NODE{type}{title}_$field";

my ($p1, $p2) = ($query->param($name.'1'), $query->param($name.'2'));
my $str;

if ($p1 or $p2) {
	if ($p1 eq $p2) {
		#update the node
		$$NODE{$field} = $p1;
		updateNode $NODE, $USER;	

		$str .= "Passwords updated.  Please ".linkNodeTitle("login", "", {user => $$USER{title}})." with this new password<br>";
	} else {
		$str .= "Passwords don't match!<br>";
	}
}


$str . $query->password_field($name."1", $$NODE{$field},8,8) .
	'<br>' .$query->password_field($name."2", $$NODE{$field},8,8);
