NODE.field:
my ($objectName) = @_;
my $str;

my $verify_failed = $this->get_message;

unless ( ref $verify_failed && ref $verify_failed eq 'HASH' ) {
$verify_failed = {};
}

if($objectName)
{
  $str = $verify_failed->{$objectName}
     if(exists $verify_failed->{$objectName});
}
elsif(%$verify_failed)
{
  # if no object name was given, we will just report if any verify
  # errors were encountered.
  $str = "Form verification failed."
}
$str;
