NODE.field:
 	my ($PARAM) = @_;
return if (($$USER{title} eq "Guest User") and not($HTMLVARS{GUshowsearchform}) ); 
 	my $str = $query->start_form .
 	$query->textfield(-name => "node",
                 -default => "",
                 -size => 50,
                 -maxlength => 80) .
 	$query->submit("go_button", "go");

 #	foreach (keys %$PARAM) {  $str .= $query->hidden($_, $$PARAM{$_}); } 

 	$str.= $query->end_form unless $PARAM eq 'noendform';

$str;
