NODE.field:
my $date = localtime()."";
my $str;
$str = $date . "<br>";
return $str."(uptime unsupported under Win32)" if $^O eq "MSWin32";

my $uptime = `uptime`;
my @uptime;

$uptime =~ s/^\s*(.*?)\s*$/$1/;
@uptime = split /,?\s+/, $uptime;

shift @uptime;

$str .= "@uptime[0..3]" . "<br>";
#$str .= "@uptime[4..5]" . "<br>";
#$str .= "@uptime[6..7]" . "<br>";


foreach (@uptime[-3..-1]){
	if ($_ > 1.0) {
		$_ = "<font color=#CC0000>" . $_ ."</font>, ";
	}else{
		$_ .= ", "; 
	}
}
$str .= "@uptime[-3..-1]". "<br>";

$str;
