NODE.field:
#given a setting name or id, create a checkbox tied to
#that settings value.  You can optionally send a label
#
my ($setting, $field, $labelon, $labeloff) = @_;
my $query = $this->get_query;
my $DB = $this->get_nodebase;
my $USER = $this->get_user;

$labelon ||= "on";
$labeloff ||= "off";


my $S;
$S = $DB->getNode($setting) if $setting =~ /^\d+$/;
$S = $DB->getNode($setting, 'setting');

$S or return;
return unless $S->hasAccess($USER);

my $V = $S->getVars();
my $param = "setfield".getId($S).$field;

if (defined $query->param($param)) {
  $$V{$field} = $query->param($param);
  $S->setVars($V);
  $S->update($USER);
}




$query->radio_group($param, [0,1], $$V{$field}, '', {0 => $labeloff, 1 => $labelon});
