NODE.field:
local *getModuleFunctions = sub {
    my ($modname) = @_;

    no strict;
    local *stash;
    *stash = *{ "${modname}::" };
    unless (keys %stash) {
        #we haven't loaded the module.  Make it so.
        eval "use $modname";
        *stash = *{ "${modname}::" };
    }
    my @modfuncs;

    use Devel::Peek "CvGV";
    foreach(keys %stash) {
        my $glob = CvGV(\&{$stash{$_}});

        push (@modfuncs, $_)
            if (defined &{ $stash{$_} }
                    and $glob eq "\*$modname\:\:$_");
    }
    use strict;
    return @modfuncs;
};








my ($modname) = @_;


my %funcs;
my $rows;
my $i;
my $str;

 $str .= "<table width=100% border=0 cellpadding=1 cellspacing=2>";


  my $colwidth = "100%";

my @modfuncs = sort {$a cmp $b} getModuleFunctions($modname);
$funcs{$modname} = \@modfuncs;
$rows = int(@modfuncs);
$str.="<TH width=$colwidth colspan = 5 bgcolor=black><font color=white>$modname</white></TH>";

my $count=0;
while ($count < $rows) 
{
    $str.="<TR>";
    for ($i=1; $i<=5; $i++){
        $str.= "<TD bgcolor=#cbcbcb>";
        if (int (@{ $funcs{$modname} }) > $count)
        { $str.=linkNode($NODE, $funcs{$modname}[$count],
                { choicefunc => "$modname\:\:".$funcs{$modname}[$count] });
        } else
        { $str.="&nbsp;"; }


        $str.="</TD>";
        $count++;
    }
    $str.="</TR>\n"
}


return $str.="</table>";
