NODE.code:
use Everything::FormMenu;

if($query->param('settheme'))
{
  # update the users's settings.
  $$VARS{preferred_theme} = $query->param('settheme');
  getTheme();
}

my $menu = new Everything::FormMenu();

$menu->addType('theme', 1, $USER, 'x');
$menu->addType('themesetting', 1, $USER, 'x');

$menu->writePopupHTML($query, "settheme", $$VARS{preferred_theme});
