NODE.field:
my $menu = $this->new_form_object('FormMenu');
my $NODE = $this->get_node;
my $query = $this->get_query;
my $VARS = $this->get_vars;

my $html = $menu->genObject($query, $NODE, 'vars:preferred_theme', "settheme");

$menu->addType('theme',$NODE, 'x');
$menu->addType('themesetting', $NODE, 'x');
$menu->sortMenu('labels');

$html .= $menu->genPopupMenu($query, "settheme", $$VARS{preferred_theme});

return $html;
