NODE.code:
my $str = "<p align=right>
<img src=images/e2.gif><BR>
<font size=2>
[ ";

if (getId($USER) == $HTMLVARS{guest_user}) {
	$str .= linkNodeTitle ("login", $NODE, "Log in") 	
	  . " | " . linkNodeTitle("Create a new user");
} else {
	$str .= linkNode ($HTMLVARS{default_node}, "log $$USER{title} out", {op => 'logout'})
		. " | " . linkNode ($USER);
	$str .= " | " . linkNodeTitle ("user settings");
}


my ($GNL) = getNodeWhere({title=>'Gigantic Node Lister'}, $NODETYPES{restricted_superdoc}, "", 'light');
$str .= " | " . linkNode ($GNL) if canReadNode ($USER, $GNL);

my ($GCL) = getNodeWhere({title=>'Gigantic Code Lister'}, $NODETYPES{restricted_superdoc}, "", 'light');
$str .= " | " . linkNode ($GCL) if canReadNode ($USER, $GCL);

my ($ROOT) = getNodeWhere({title => 'root'}, $NODETYPES{user});
my ($ss) = getNodeWhere ({title => 'system settings'}, $NODETYPES{setting});
$str .= " | " . linkNode ($ss) if ($USER eq $ROOT);


$str .= " | " . linkNode ($HTMLVARS{default_node});
$str . " ] <p></font>";
