NODE.code:
my $str = "

<font size=2>
[ ";

if (getId($USER) == $HTMLVARS{guest_user}) {
	$str .= linkNodeTitle ("login", $NODE, "Log in") 	
	  . " | " . linkNodeTitle("Create a new user");
} else {
	$str .= linkNode ($HTMLVARS{default_node}, "log $$USER{title} out", {op => 'logout'})
		. " | " . linkNode ($USER);
	$str .= " | " . linkNodeTitle ("user settings");
}


my ($GNL) = $DB->getNodeWhere({title=>'Gigantic Node Lister'},
  $DB->getType('restricted_superdoc'), "", 'light');
$str .= " | " . linkNode ($GNL) if canReadNode ($USER, $GNL);

my ($GCL) = $DB->getNodeWhere({title=>'Gigantic Code Lister'},
  $DB->getType('restricted_superdoc'), "", 'light');
$str .= " | " . linkNode ($GCL) if canReadNode ($USER, $GCL);

my ($ROOT) = $DB->getNodeWhere({title => 'root'},
  $DB->getType('user'));
my ($ss) = $DB->getNodeWhere ({title => 'system settings'},
  $DB->getType('setting'));
$str .= " | " . linkNode ($ss) if ($USER eq $ROOT);


$str .= " | " . linkNode ($HTMLVARS{default_node});
$str . " ] <p></font>";
