NODE.field:
my ($delimiter) = @_;
my $NODE = $this->get_node;
my %HTMLVARS = %{ $this->get_htmlvars };
my $USER = $this->get_user;
my $DB = $this->get_nodebase;

$delimiter ||= " | ";
my $str;
my @links;


push @links, $this->link_node ($HTMLVARS{default_node}, "Log $$USER{title} out", {op => 'logout'});
push @links, $this->link_node ($USER, "Edit $$USER{title}'s settings", { 'displaytype' => 'edit' });

my $EN = $DB->getNode('Epicenter Nodes', 'nodegroup');

foreach (@{ $$EN{group} }) {
  my $N = $DB->getNode( $_ );
  push @links, $this->link_node($N) if $N->hasAccess($USER, 'r');
}


$str = join "$delimiter\n", @links;
