NODE.field:
# This is used to update the permissions fields for nodes
# and the "default" permissions fields on a nodetype.  If
# this for a nodetype (default permissions) pass 1 (true)
# to this code.

my ($forType) = @_;
my @who = ( "author", "group", "other", "guest" );
my @what = ( "read", "write", "exec", "delete", "create" );
my $item;
my $perms;
my $perm;
my $field;
my $gfound = 0;
my $found;


foreach my $user (@who)
{
  $perms = "";
  $found = 0;

  foreach my $access (@what)
  {
    # We need to append a default 'i' for the create flag for non-type nodes 
	$perms .= "i", next
      if (($access eq "create") and ($user ne "author") and (!$forType));

    $item = "$user" . "_" . $access;
    $item = "default_" . $item if($forType);

    if($perm = $query->param($item))
    {
      $perms .= $perm;
      $gfound = 1;
      $found = 1;
    }
  }

  $field = $user . "access";
  $field = "default" . $field if($forType);

  $$NODE{$field} = $perms if($found);
}

$NODE->update($USER) if($gfound);

""; # don't return anything
