NODE.field:
my @params;
my $THEME = $this->get_theme;
my $bgcolor ||= $$THEME{bodyBgcolor};
$bgcolor ||= "#FFFFFF";
push @params, "bgcolor='$bgcolor'";

my $textcolor = $$THEME{bodyTextcolor};
$textcolor ||= "#000000";
push @params, "text='$textcolor'";

my $linkcolor = $$THEME{bodyLinkcolor};
$linkcolor ||= "#993333";
push @params, "link='$linkcolor'";

my $vlinkcolor = $$THEME{bodyVlinkcolor};
$vlinkcolor ||= "#333399";
push @params, "vlink='$vlinkcolor'";

"<BODY " . join(' ', @params). ">";
