NODE.field:
my @params;
my $bgcolor ||= $$THEME{bodyBgcolor};
$bgcolor ||= "#FFFFFF";
push @params, "bgcolor='$bgcolor'";

my $textcolor = $$THEME{bodyTextcolor};
$textcolor ||= "#000000";
push @params, "text='$textcolor'";

my $linkcolor = $$THEME{bodyLinkcolor};
$linkcolor ||= "#993333";
push @params, "link='$linkcolor'";

my $vlinkcolor = $$THEME{bodyVlinkcolor};
$vlinkcolor ||= "#333399";
push @params, "vlink='$vlinkcolor'";

my $lmargin = $$THEME{bodyLMargin};
$lmargin = 5 unless(defined $lmargin);
push @params, "leftmargin='$lmargin' marginwidth='$lmargin'";

my $tmargin = $$THEME{bodyTMargin};
$tmargin = 5 unless(defined $tmargin);
push @params, "topmargin='$tmargin' marginheight='$tmargin'";

"<BODY " . join(' ', @params). ">";
