NODE.field:
my ($var, @TYPES) = @_;

$var or return;

my ($name) = "set$var";

@TYPES or return;  #if no explicit types, quit

foreach (@TYPES) { $_ = $DB->getType($_); }  #replace w/ node refs
my $NODELIST = $DB->selectNodeWhere ({ type_nodetype => \@TYPES },
  "", "title");

my $str;

my (@titlelist, %items);
$items{"0"} = "(default)";

push (@titlelist, "0");
       foreach my $N (@$NODELIST) {
                $N = $DB->getNodeById($N, 'light');
                my $id = getId $N;
		push @titlelist, $id;
		$items{$id} = $$N{title};
        }

my $SELECT = $$VARS{$var};

if (my $q = $query->param("set$var")) {$$VARS{$var} = $q;}
elsif ($query->param("sexisgood")){ $$VARS{$var}=""; } 

$query->popup_menu($name, \@titlelist, $SELECT, \%items);
