NODE.field:
my ($displaytype, $title, $width, $height) = @_;
my $NODE = $this->get_node;

$title||=$$NODE{title};
$width||=300;
$height||=400;

"<SCRIPT language=\"javascript\">
	function launchTheWinda() {
	window.open('" .
		$this->url_gen({node_id => $NODE->get_node_id,
			displaytype => $displaytype}, "noquotes") .
		"','". $NODE->get_node_id ."',
		'width=$width,height=$height,scrollbars=yes');	
	}
</SCRIPT>
<A href=\"javascript:launchTheWinda()\">$title</a>";
