NODE.field:
[%

my $template = q{
<table class="displayMail">
<tr><th>To:</th><td>[[author_user]]
</td></tr>
<tr><th>From:</th><td>[[from_address]]</td></tr></table>

<pre>[[doctext]]</pre>};

my $from_address = $this->get_requested_node->get_from_address ||  "<i>nobody</i>";

my $author_user = $this->get_requested_node->get_title;

my $doctext = $this->encode_html( $this->get_requested_node->get_doctext );

$template =~ s/\[\[author_user\]\]/$author_user/;
$template =~ s/\[\[from_address\]\]/$from_address/;
$template =~ s/\[\[doctext\]\]/$doctext/;

$template;

%]
