NODE.field:
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;
my $query = $this->get_query;
my $THEME = $this->get_theme;
my %HTMLVARS = %{ $this->get_htmlvars };
my $VARS = $this->get_vars;
my $DB = $this->get_nodebase;
my %GLOBAL;

 unless ($$NODE{group} and @{ $$NODE{group} }) {
	"<i>This nodeball is empty</i>";
} else {
	my $GROUP = $$NODE{group};
	my $str = '<ul>';
	foreach (@$GROUP) {
		my $N = $DB->getNodeById($_, 'light');
		$str .= "<li>" . linkNode ($N) . "($$N{type}{title})" . "</li>";
	}
	$str .= '</ul>';
}

%]
[{displayvars}]
