[%
my @nodes = getNodeWhere({author_user => getId($NODE)},"","",'light');
my $str;
foreach (@nodes) {
  next if getId($_) == getId($NODE);
  $str.="<li>".linkNode($_)."($$_{type}{title})</li>\n";
}
$str = "Nodes authored by $$NODE{title}:<ul>".$str."</ul>"
  ."<br>(".int(@nodes)." in total)" if $str;
$str ||= "<i>no nodes maintianed by $$NODE{title}</i>\n";
%]
