[%
my $nodes = $DB->selectNodeWhere({author_user => getId($NODE)},
  undef, undef, undef, undef, undef, 'nodetableonly');
my $str;
foreach (@$nodes)
{
  my $id = $_;
  my $N = getNode($_);
  next if($id == getId($NODE));
  $str.="<li>".linkNode($N)."($$N{type}{title})</li>\n";
}

$str = "Nodes authored by $$NODE{title}:<ul>".$str."</ul>"
  ."<br>(".int(@$nodes)." in total)" if $str;
$str ||= "<i>no nodes maintianed by $$NODE{title}</i>\n";
%]

<p>
[%
return unless($NODE->hasAccess($USER, "w"));

linkNode($NODE, 'edit', { displaytype => 'edit' });
%]
