NODE.field:
[%

if ($$USER{inside_workspace} != $$NODE{node_id}) {
  return linkNode($NODE, "Join this workspace", { op => 'changeworkspace', workspace_me => 'true' });
}

linkNode($NODE, "Remove me from this workspace", { op => 'changeworkspace', workspace_me => 'false'});

%]
<p>
Users in this workspace:
[%
  my (@U) = @{ getNodeWhere({inside_workspace => $$NODE{node_id}}, 'user') || [] };
  return "<p><i>none</i>" unless @U;
  my $str;
  foreach (@U) { $str.="<li>".linkNode($_) }
  $str;
%]

<p>

Nodes revised in this workspace:
[%
my $str;
my $V = $NODE->getVars;

my $isInWS;
if (exists($DB->{workspace})) {
	$isInWS =  $DB->{workspace}{node_id} == $$NODE{node_id};
}

$str.= openform() ."
<INPUT TYPE=hidden NAME=op VALUE=commitworkspace>" if $isInWS;

return unless exists $DB->{workspace} and $DB->{workspace}{node_id} == $$NODE{node_id};
return "<p><i>none</i>" unless keys %$V;

foreach (keys %$V) {
  $str.="<li>";
  $str.=$query->radio_group("commit_$_", ['commit', 'discard', 'unchanged'], 'unchanged')." " if $isInWS;
  $str.= linkNode($_);
}
$str.="<br>".$query->submit("trash", "commit changes")."</FORM>" if $isInWS;
$str;
%]
