NODE.field:
[%
my $NODE = $this->get_node;
my $THEME = $this->get_theme;
my $query = $this->get_query;
my $USER = $this->get_user;
my %GLOBAL;
my $str;

#Setup

# This provides links to display, edit, clone, or delete
# the current node if the user has the authority to do so.  
my $disp = $query->param("displaytype");
$disp ||= "display";

$GLOBAL{adminboxLinks} = {};

# If you want to have other default display links in the
# adminbar, add them to this array.
my @displaytypes = ("display", "edit", "permissions" );
# Now theme stuff
$GLOBAL{adminboxBG} = $$THEME{adminboxBG};
$GLOBAL{adminboxBG} ||= "#ffffff";
$GLOBAL{adminboxLite} = $$THEME{adminboxLite};
$GLOBAL{adminboxLite} ||= "#ffcccc";
$GLOBAL{adminboxDark} = $$THEME{adminboxDark};
$GLOBAL{adminboxDark} ||= "#ff9999";

foreach my $type (@displaytypes)
{
  if ($disp eq $type)
  {
    $GLOBAL{adminboxLinks}{$type} = 
      qq{<img src="images/icon_${type}_current.gif" alt="$type" height="20" width="48" style="border-style: none" />};
  } 
  else
  {
    $GLOBAL{adminboxLinks}{$type} =  $this->link_node ($NODE,
      qq{<img src="images/icon_$type.gif"  alt="$type" height="20" width="48" style="border-style: none" />}, 
      {displaytype => $type}) . " ";
  }
}




$str.=qq{<table width="100%" cellpadding="3" style="background-color: $GLOBAL{adminboxBG}" cellspacing="1">
  <tr style="background-color: $GLOBAL{adminboxBG}"> 
    <td align="center" style="border-style: none; background-color: $GLOBAL{adminboxLite}">
$GLOBAL{adminboxLinks}{display}
    </td>};

$str.=qq|    <td style="background-color: $GLOBAL{adminboxDark}" align="center">
$GLOBAL{adminboxLinks}{edit}
    </td>|;
$str.=qq|<td style="background-color: $GLOBAL{adminboxLite}" align="center">
$GLOBAL{adminboxLinks}{permissions}
    </td>
  </tr>|;

my $node_title = $NODE->get_title;

my $CLONER = $DB->getNode('Node Cloner', 'restricted_superdoc');
my $clone_url = $this->url_gen({'srcnode_id' => $this->get_node->get_node_id, 'newname' => 'NEWNAME', 'node_id' => $$CLONER{node_id} }, 'no quotes');

$str.=qq|  <tr>
    <td style="background-color: $GLOBAL{adminboxDark}" align="center">
      <a href="javascript: cloneNode('$node_title','$clone_url');">
       <img src="images/icon_clone.gif" alt="clone" height="20" width="48" style="border-style: none" /></a>
    </td>
    <td style="background-color: $GLOBAL{adminboxLite}" align="center">|;
 $str.= $this->clonetotype();
$str.=qq|    </td>|;
$str.=qq|   <td style="background-color: $GLOBAL{adminboxDark}" align="center">|;

if($NODE->hasAccess($USER, "d"))
{
 my $title = $this->jsescape($NODE->get_title); 
 my $goto = $this->url_gen({node_id => $NODE->get_node_id, op => "nuke"}, 'no quotes');
  $str .= qq{<a href="javascript: confirmDelete('$title','$goto');">};
  $str .= qq{<img src="images/icon_delete.gif" alt="delete" height="20" width="48" style="border-style: none" /></a>};
}
else
{
  $str .= "&nbsp;";
}
$str .=q|
    </td>
  </tr>|;

#another row for undo/delete
my $workspace = 0;
my $disp = $query->param('displaytype');
$disp ||= 'display';

if ($$NODE{type}{maxrevisions})
{
  my $undo = $NODE->undo($USER, 0, 'test');
  my $redo = $NODE->undo($USER, 1, 'test'); 

  if ($undo or $redo)
  {
    $str.=qq{<tr><td style="background-color: $GLOBAL{adminboxLite}" align="center">};
    if ($undo) {
      $str.= $this->link_node($NODE,
        qq{<img src="images/icon_undo.gif" alt="undo" height="20" width="48" style="border-style: none" />},
        { op => 'undo', displaytype => $disp });
    } else {
      $str.="&nbsp;";
    }

    $str.="</td><td style=\"background-color: $GLOBAL{adminboxDark}\" align=\"center\">";

    if ($redo) {
       $str.= $this->link_node($NODE,
         qq{<img src="images/icon_redo.gif" alt="undo" height="20" width="48" style="border-style: none" />},
         { op => 'redo', displaytype => $disp });
    } else {
      $str.="&nbsp;";
    }
  
   $str.="</td><td style=\"background-color: $GLOBAL{adminboxLite}\" align=\"center\">&nbsp;</td></tr>";

  }
}

$str .= q|</table>|;
%]
