NODE.field:
[%
#Setup

includeJS('cloneNode', 'confirmDelete');

# This provides links to display, edit, clone, or delete 
# the current node if the user has the authority to do so.  
my $disp = $query->param("displaytype");
$disp ||= "display";

$GLOBAL{adminboxLinks} = {};

# If you want to have other default display links in the
# adminbar, add them to this array.
my @displaytypes = ("display", "edit", "permissions" );

foreach my $type (@displaytypes)
{
  if ($disp eq $type)
  {
    $GLOBAL{adminboxLinks}{$type} = 
      "<img src=images/icon_".$type."_current.gif alt=".$type." border=0 height=\"20\" width=\"48\">";
  } 
  else
  {
    $GLOBAL{adminboxLinks}{$type} =  linkNode ($NODE,
      "<img src=images/icon_".$type.".gif  alt=".$type." border=0 height=\"20\" width=\"48\">", 
      {displaytype => $type}) . " ";
  }
}

# Now theme stuff
$GLOBAL{adminboxBG} = $$THEME{adminboxBG};
$GLOBAL{adminboxBG} ||= "#ffffff";
$GLOBAL{adminboxLite} = $$THEME{adminboxLite};
$GLOBAL{adminboxLite} ||= "#ffcccc";
$GLOBAL{adminboxDark} = $$THEME{adminboxDark};
$GLOBAL{adminboxDark} ||= "#ff9999";
"";
%]

<table width=100% border=0 cellpadding=3 bgcolor=["$GLOBAL{adminboxBG}"] cellspacing=1>
  <tr bgcolor=["$GLOBAL{adminboxBG}"]> 
    <td bgcolor='["$GLOBAL{adminboxLite}"]' align='center'>
      ["$GLOBAL{adminboxLinks}{display}"]
    </td>
    <td bgcolor='["$GLOBAL{adminboxDark}"]' align='center'>
      ["$GLOBAL{adminboxLinks}{edit}"]
    </td>
    <td bgcolor='["$GLOBAL{adminboxLite}"]' align='center'>
      ["$GLOBAL{adminboxLinks}{permissions}"]
    </td>
  </tr>
  <tr>
    <td bgcolor='["$GLOBAL{adminboxDark}"]' align='center'>
      <a href='javascript: cloneNode();'>
       <img src=images/icon_clone.gif alt=clone border=0 height="20" width="48"></a>
    </td>
    <td bgcolor='["$GLOBAL{adminboxLite}"]' align=center>
      [% clonetotype() %]
    </td>
   <td bgcolor='["$GLOBAL{adminboxDark}"]' align=center>
[%
my $str;
if($NODE->hasAccess($USER, "d"))
{
  $str .= "<a href='javascript: confirmDelete();'>";
  $str .= "<img src='images/icon_delete.gif' alt='delete' border=0 height=\"20\" width=\"48\"></a>";
}
else
{
  $str .= "&nbsp;";
}
$str; %]
    </td>
  </tr>
[%
#another row for undo/delete
my $workspace = 0;
my $disp = $query->param('displaytype');
$disp ||= 'display';
my $str;

if ($$NODE{type}{maxrevisions})
{
  my $undo = $NODE->undo($USER, 0, 'test');
  my $redo = $NODE->undo($USER, 1, 'test'); 

  if ($undo or $redo)
  {
    $str.="<tr><td bgcolor='$GLOBAL{adminboxLite}' align=center>";
    if ($undo) {
      $str.=linkNode($NODE,
        "<IMG src=images/icon_undo.gif alt=\"undo\" border=0 height=\"20\" width=\"48\">",
        { op => 'undo', displaytype => $disp });
    } else {
      $str.="&nbsp;";
    }

    $str.="</td><td bgcolor='$GLOBAL{adminboxDark}' align=center>";

    if ($redo) {
       $str.=linkNode($NODE,
         "<IMG src=images/icon_redo.gif alt=\"undo\" border=0 height=\"20\" width=\"48\">",
         { op => 'redo', displaytype => $disp });
    } else {
      $str.="&nbsp;";
    }

   $str.="</td><td bgcolor='$GLOBAL{adminboxLite}' align=center>&nbsp;</td></tr>";

  }
}

$str;
%]
</table>
