NODE.field:
function cloneNode()
{
  var newname = prompt("Cloning node '[% $this->jsescape($this->get_node->get_title) %]'\nEnter a new name:", "");

  if(newname)
  {
    var url = [% 
        my $CLONER = $DB->getNode('node cloner', 'restricted_superdoc');
        $this->url_gen({'srcnode_id' => $this->get_node->get_node_id, 'newname' => 'NEWNAME',
          'node_id' => $$CLONER{node_id} }); %];
    
    url = url.replace(/NEWNAME/, newname);
    url = url.replace(/ /g, "%20");
    parent.location = url;
  }
}
