NODE.field:
my $NODE = $this->get_node;
my $DB = $this->get_nodebase;
function cloneNode()
{
  var newname = prompt("Cloning node '[% $this->jsescape($$NODE{title}) %]'\nEnter a new name:", "");

  if(newname)
  {
    var url = [% 
        my $CLONER = $DB->getNode('node cloner', 'restricted_superdoc');
        urlGen({'srcnode_id' => $$NODE{node_id}, 'newname' => 'NEWNAME',
          'node_id' => $$CLONER{node_id} }); %];

    url = url.replace(/NEWNAME/, newname);
    url = url.replace(/ /g, "%20");
    parent.location = url;
  }
}
