NODE.field:
function findFormObject(name, doc)
{
  if(!doc) doc = document;
  var flen = doc.forms.length;
  for(var i = 0; i < flen; i++)
  {
    var f = doc.forms[i];
    var ilen = f.elements.length;
    for(var j = 0; j < ilen; j++)
    {
      var o = f.elements[j];
      if(o.name == name)
        return o;
    }
  }

  alert("Could not find form object '" + name + "'!");
  return null;
}
