NODE.field:
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;
my $query = $this->get_query;
my $THEME = $this->get_theme;
my %HTMLVARS = %{ $this->get_htmlvars };
my $VARS = $this->get_vars;
my $DB = $this->get_nodebase;
my %GLOBAL;


my @links = @{ $NODE->selectLinks("food") };
my $str = "";

foreach (@links)
{
  next if ($$_{linktype});

  my $N = $DB->getNode($$_{to_node}, 'light');
  next unless $N;
  $str .= "  <li>".$this->link_node($N)."($$N{type}{title})</li>\n" if $N->hasAccess($USER, 'r');  
}

return "No Links" unless($str ne "");

$str = "<ol>\n" . $str  . "\n</ol>";

%]
