NODE.nlcode:
[%my $limit = 15;
$limit = int($$VARS{newnodes_limit}) if (exists $$VARS{newnodes_limit});

my $str;
my $csr = $DB->getDatabaseHandle()->prepare("
	SELECT * FROM node
	ORDER BY createtime DESC
	LIMIT $limit");

$csr->execute or return "SHIT";
while(my $N = $csr->fetchrow_hashref){
	$str .= linkNode($N) . "<BR>\n";	
}
$csr->finish;
$str;



%]
